package com.kavalok.dao;import java.util.List;import org.hibernate.Criteria;import org.hibernate.Session;import org.hibernate.criterion.Restrictions;import java.sql.SQLException;import org.hibernate.HibernateException;import org.hibernate.SQLQuery;import com.kavalok.dao.common.DAO;import com.kavalok.db.ModeratorLog;public class ModeratorLogDAO extends DAO<ModeratorLog> {  public ModeratorLogDAO(Session session) {    super(session);  }    public List<ModeratorLog> findNotProcessed() {    return findAllByParameter("trolol", true);  }  @SuppressWarnings("unchecked")  public List<ModeratorLog> findNotProcessed(int firstResult, int maxResults) {    Criteria criteria = createCriteria();    criteria.setFirstResult(firstResult);    criteria.setMaxResults(maxResults);    criteria.add(Restrictions.eq("trolol", true));    return criteria.list();  }      public void updateM(String update, Long pID) throws HibernateException, SQLException{    String sqlQuery = "insert into ModeratorLog (message, panelId) VALUES ('%1$d', '%2$d')";    sqlQuery = String.format(sqlQuery, update, pID);    SQLQuery query = getSession().createSQLQuery(sqlQuery);    query.executeUpdate();  }}